import React, { useState } from 'react';
import axios from 'axios';

const ArchiCard = ({ title, endpoint, requirement }) => {
  const [data, setData] = useState("");
  const [loading, setLoading] = useState(false);

  const handleGen = async () => {
    if (!requirement) return alert("Enter requirements first!");
    setLoading(true);
    try {
      const res = await axios.post(`http://localhost:8000/generate/${endpoint}`, { requirement });
      setData(res.data.output);
    } catch (err) {
      setData("Error: Ensure backend and Ollama are running.");
    }
    setLoading(false);
  };

  return (
    <div className="card">
      <div className="header">
        <h3>{title}</h3>
        <button onClick={handleGen} disabled={loading}>
          {loading ? "Thinking..." : "Generate Section"}
        </button>
      </div>
      <pre className="content">{data || "Click generate to see results..."}</pre>
    </div>
  );
};

export default function App() {
  const [requirement, setRequirement] = useState("");

  return (
    <div className="container">
      <h1>Requirement-to-Architecture</h1>
      <textarea 
        placeholder="Type your project idea (e.g., E-commerce for pet food)..."
        onChange={(e) => setRequirement(e.target.value)}
      />
      <div className="grid">
        <ArchiCard title="System Architecture" endpoint="architecture" requirement={requirement} />
        <ArchiCard title="Database Schema" endpoint="database" requirement={requirement} />
        <ArchiCard title="API & Requirements" endpoint="api" requirement={requirement} />
      </div>
    </div>
  );
}
